import React from 'react';
import Header from './Header';
import CategoryTag from './CategoryTag';
import PostList from './PostList';
import './css/HomePage.css';

const HomePage = ({ user, onLogout }) => {
  const categories = [
    "Kubernetes", "Serverless", "DevOps", "AWS", "Azure",
    "Google Cloud", "Docker", "Python", "Microservices",
    "CI/CD", "Terraform", "Machine Learning"
  ];

  return (
    <div className="home-page">
      <main className="main-content">
        <div className="content-wrapper">
          <div className="left-column">
            <h1 className="page-title">Tools & Cloud</h1>
            <p className="page-subtitle">Thoughts on the future of cloud technology, from the people and teams creating it.</p>
            <div className="categories-section">
              <h2 className="section-title">Categories</h2>
              <div className="categories-list">
                {categories.map((category, index) => (
                  <CategoryTag key={index} category={category} />
                ))}
              </div>
            </div>
          </div>
          <div className="center-column">
            <PostList />
          </div>
          <div className="right-column">
            <div className="marketing-card">
              <h3 className="marketing-title">Marketing Home</h3>
              <p>Have a marketing request?</p>
              <a href="/social-media-request" className="marketing-link">Social media request</a>
              <a href="/email-request" className="marketing-link">Email request</a>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
