import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import CommentSection from './CommentSection';
import DOMPurify from 'dompurify';
import './css/PostPage.css';

const PostPage = ({ user }) => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [recentPosts, setRecentPosts] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Fetch the current post and surrounding posts
  useEffect(() => {
    fetchPostAndSurroundingPosts(id);
  }, [id]);

  // Fetch the post and its surrounding posts
  const fetchPostAndSurroundingPosts = async (postId) => {
    try {
      // Fetch the specific post by ID
      const postResponse = await fetch(`http://localhost:5000/api/posts/${postId}`);
      const postData = await postResponse.json();
      setPost(postData);

      // Fetch surrounding posts (including the current post)
      const surroundingResponse = await fetch(`http://localhost:5000/api/posts/surrounding/${postId}`);
      const surroundingData = await surroundingResponse.json();
      setRecentPosts(surroundingData);
    } catch (error) {
      setError('Error fetching posts');
      console.error('Error fetching posts:', error);
    }
  };

  const handleRecentPostClick = (postId) => {
    // Fetch surrounding posts for the clicked post
    fetchPostAndSurroundingPosts(postId);
    navigate(`/post/${postId}`);
  };

  if (error) return <div className="error-message">{error}</div>;
  if (!post) return <div className="loading-message">Loading...</div>;

  return (
    <div className="post-page-container">
      <div className="back-arrow" onClick={() => navigate('/')}>
        ‚Üê Back
      </div>

      <div className="post-page">
        <aside className="sidebar">
          <h2 className="sidebar-title">Recent Posts</h2>
          <ul className="recent-posts-list">
            {recentPosts.map((recentPost) => (
              <li
                key={recentPost.id}
                className={
                  recentPost.id === post.id ? 'active recent-post-item' : 'recent-post-item'
                }
              >
                <Link
                  to={`/post/${recentPost.id}`}
                  className="recent-post-link"
                  onClick={() => handleRecentPostClick(recentPost.id)}
                >
                  {recentPost.title}
                </Link>
              </li>
            ))}
          </ul>
        </aside>

        <div className="post-content">
          <div className="post-category">
            {post.category && <p>{post.category}</p>}
          </div>
          <h1 className="post-title">{post.title}</h1>
          <p className="post-date">
            {new Date(post.createdAt).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric',
              hour: '2-digit',
              minute: '2-digit',
            })}
          </p>
          <p className="view-count">üëÅ {post.readCount || 0} views</p>
          {post.imageUrl && (
            <div className="post-image-container">
              <img src={post.imageUrl} alt={post.title} className="post-image" />
            </div>
          )}
          <div
            dangerouslySetInnerHTML={{
              __html: DOMPurify.sanitize(post.content),
            }}
            className="post-text-content"
          />
          <CommentSection postId={id} user={user} />
        </div>
      </div>
    </div>
  );
};

export default PostPage;
