import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import CategoryTag from './CategoryTag';
import PostList from './PostList';
import './css/HomePage.css';

const HomePage = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const categories = [
    "Kubernetes", "Serverless", "DevOps", "AWS", "Azure",
    "Google Cloud", "Docker", "Python", "Microservices",
    "CI/CD", "Terraform", "Machine Learning"
  ];

  return (
    <div className="home-page">
      <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
      <main className={`main-content ${isSidebarOpen ? 'shifted' : ''}`}>
        <div className="explore-section">
          <h1 className="explore-title">Explore</h1>
          <p className="explore-subtitle">Find the best blogs on cloud technology</p>
          <div className="category-tags">
            {categories.map((category, index) => (
              <CategoryTag key={index} category={category} />
            ))}
          </div>
        </div>
        <PostList />
      </main>
    </div>
  );
};

export default HomePage;
