import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import CommentItem from './CommentItem';
import './css/CommentSection.css';

const socket = io('http://localhost:5000'); // Update with your backend URL

const CommentSection = ({ postId, user }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/comments/${postId}`);
        const data = await response.json();
        setComments(data);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchComments();

    socket.emit('joinPost', postId);

    socket.on('newComment', (comment) => {
      setComments((prevComments) => [...prevComments, comment]);
    });

    return () => {
      socket.emit('leavePost', postId);
      socket.off('newComment');
    };
  }, [postId]);

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (newComment.trim()) {
      socket.emit('createComment', { postId, content: newComment, userId: user?.userId });
      setNewComment('');
    }
  };

  const handleReply = (parentId, replyText) => {
    if (replyText.trim()) {
      socket.emit('createComment', { postId, content: replyText, parentId, userId: user?.userId });
    }
  };

  const handleLike = (commentId) => {
    if (!user) {
      alert('Please log in to like a comment.');
      return;
    }

    // Emit the like action to the server
    socket.emit('likeComment', { commentId });

    // Update the like count immediately on the frontend
    setComments((prevComments) =>
      prevComments.map((comment) =>
        comment.id === commentId ? { ...comment, likes: comment.likes + 1 } : comment
      )
    );
};


  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <ul>
        {comments.length > 0 ? (
          comments.map((comment, index) => (
            <CommentItem
              key={index}
              comment={comment}
              onReply={handleReply}
              onLike={handleLike}
              user={user}
            />
          ))
        ) : (
          <p>No comments available.</p>
        )}
      </ul>
      <form onSubmit={handleCommentSubmit}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
          disabled={!user}
        />
        <button type="submit" disabled={!user}>Post</button>
      </form>
    </div>
  );
};

export default CommentSection;
