import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp as solidThumbsUp } from '@fortawesome/free-solid-svg-icons';
import './css/CommentItem.css';

const CommentItem = ({ comment, onReply, onLike, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');

  const userId = user?.user?.userId || null;

  // Check if the current user has liked this comment
  const isLikedByCurrentUser = comment.likedUsers.includes(userId);

  const handleLikeClick = () => {
    if (userId) {
      onLike(comment.id, isLikedByCurrentUser); // Pass the current like status
    } else {
      alert('Please log in to like this comment.');
    }
  };

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (userId) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  // Function to get the first letter of the username
  const getInitial = (name) => {
    return name ? name.charAt(0).toUpperCase() : '?';
  };

  // Function to generate a random color based on userId or username
  const generateColor = (input) => {
    const colors = ['#F44336', '#E91E63', '#9C27B0', '#673AB7', '#3F51B5', '#2196F3', '#00BCD4', '#4CAF50', '#FF9800', '#795548'];
    const index = input ? input.charCodeAt(0) % colors.length : 0;
    return colors[index];
  };

  return (
    <div className="comment-container">
      <div className="comment-avatar" style={{ backgroundColor: generateColor(comment.author) }}>
        <span className="avatar-initial">{getInitial(comment.author)}</span>
      </div>
      <div className="comment-content">
        <div className="comment-header">
          <span className="comment-author">{comment.author}</span>
          <span className="comment-time">{comment.timeAgo}</span>
        </div>
        <div className="comment-text">
          <p>{comment.content}</p>
        </div>
        <div className="comment-actions">
          <span className="like-button" onClick={handleLikeClick}>
            <i className="fas fa-thumbs-up"></i> {comment.likeCount}
          </span>
          <span 
            className={`reply-text ${showReplyForm ? 'clicked' : ''}`} 
            onClick={() => setShowReplyForm(!showReplyForm)}
          >
            Reply
          </span>
        </div>

        {showReplyForm && (
          <form onSubmit={handleReplySubmit} className="reply-form">
            <input 
              type="text" 
              value={replyText} 
              onChange={(e) => setReplyText(e.target.value)} 
              placeholder="Write a reply..." 
            />
            <button type="submit">Reply</button>
          </form>
        )}

        {comment.replies && comment.replies.length > 0 && (
          <div className="comment-replies">
            {comment.replies.map((reply) => (
              <CommentItem key={reply.id} comment={reply} onReply={onReply} onLike={onLike} user={user} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
