import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import CommentSection from './CommentSection';
import DOMPurify from 'dompurify';
import './css/PostPage.css';

const PostPage = ({ user }) => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [recentPosts, setRecentPosts] = useState([]);
  const [error, setError] = useState('');
  const [totalPosts, setTotalPosts] = useState(0); // Total number of posts
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the current post
    fetch(`http://localhost:5000/api/posts/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => setPost(data))
      .catch((error) => setError(error.message));

    // Fetch total post count to calculate navigation correctly
    fetch(`http://localhost:5000/api/posts/count`)
    .then((response) => response.json())
    .then((count) => setTotalPosts(count))
    .catch((error) => console.error('Error fetching post count:', error));

  }, [id]);

  // Function to fetch 5 recent posts dynamically around a specific post
  const fetchRecentPosts = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts`);
      const data = await response.json();
      const postIndex = data.findIndex(post => post.id === parseInt(postId, 10));

      if (postIndex === -1) return;

      // Calculate start and end index to always display 5 posts
      let startIndex = Math.max(0, postIndex - 2); // Shift left if possible
      let endIndex = Math.min(totalPosts, startIndex + 5);

      setRecentPosts(data.slice(startIndex, endIndex));
    } catch (error) {
      console.error('Error fetching recent posts:', error);
    }
  };

  // Handle click on a recent post and update the recent posts list based on the clicked post
  const handleRecentPostClick = (postId) => {
    // Fetch 5 recent posts around the clicked post
    fetchRecentPosts(postId);
    navigate(`/post/${postId}`);
  };

  if (error) return <div className="error-message">{error}</div>;
  if (!post) return <div className="loading-message">Loading...</div>;

  return (
    <div className="post-page-container">
      <div className="back-arrow" onClick={() => navigate('/')}>
        ‚Üê Back
      </div>

      <div className="post-page">
        <aside className="sidebar">
          <h2 className="sidebar-title">Recent Posts</h2>
          <ul className="recent-posts-list">
            {recentPosts.map((recentPost) => (
              <li
                key={recentPost.id}
                className={
                  recentPost.id === post.id ? 'active recent-post-item' : 'recent-post-item'
                }
              >
                <Link
                  to={`/post/${recentPost.id}`}
                  className="recent-post-link"
                  onClick={() => handleRecentPostClick(recentPost.id)}
                >
                  {recentPost.title}
                </Link>
              </li>
            ))}
          </ul>
        </aside>

        <div className="post-content">
          <div className="post-category">
            {post.category && <p>{post.category}</p>}
          </div>
          <h1 className="post-title">{post.title}</h1>
          <p className="post-date">
            {new Date(post.createdAt).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric',
              hour: '2-digit',
              minute: '2-digit',
            })}
          </p>
          <p className="view-count">üëÅ {post.readCount || 0} views</p>
          {post.imageUrl && (
            <div className="post-image-container">
              <img src={post.imageUrl} alt={post.title} className="post-image" />
            </div>
          )}
          <div
            dangerouslySetInnerHTML={{
              __html: DOMPurify.sanitize(post.content),
            }}
            className="post-text-content"
          />
          <CommentSection postId={id} user={user} />
        </div>
      </div>
    </div>
  );
};

export default PostPage;
