import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './css/PostList.css';

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Fetched posts:', data);
      const publishedPosts = data.filter(post => post.status === 'Published');
      setPosts(publishedPosts);
    } catch (error) {
      console.error('Error fetching posts:', error);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleReadMoreClick = (postId, postTitle) => {
    console.log(`Navigating to post ID: ${postId}, Title: ${postTitle}`);
    navigate(`/post/${postId}`);
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="post-list">
      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} className="post-item">
            <div className="post-content">
              <h3>{post.title}</h3>
              <p>{new Date(post.createdAt).toLocaleString()}</p>
              <button onClick={() => handleReadMoreClick(post.id, post.title)}>
                Read More
              </button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
