import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './css/CommentSection.css';

const socket = io('http://localhost:5000');

const CommentSection = ({ postId, user }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/comments/${postId}`);
        const data = await response.json();
        setComments(data);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchComments();

    socket.emit('joinPost', postId);

    socket.on('newComment', (comment) => {
      setComments((prevComments) => [...prevComments, comment]);
    });

    return () => {
      socket.emit('leavePost', postId);
      socket.off('newComment');
    };
  }, [postId]);

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (user && newComment.trim()) {
      socket.emit('createComment', { postId, content: newComment, userId: user.userId });
      setNewComment('');
    } else {
      alert('Please log in to comment.');
    }
  };

  const handleLike = (commentId) => {
    if (user) {
      socket.emit('likeComment', { commentId });
    } else {
      alert('Please log in to like comments.');
    }
  };

  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <ul>
        {comments.map((comment, index) => (
          <CommentItem
            key={index}
            comment={comment}
            onReply={handleCommentSubmit}
            onLike={handleLike}
            user={user}
          />
        ))}
      </ul>
      <form onSubmit={handleCommentSubmit}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder={user ? "Write a comment..." : "Log in to write a comment..."}
          disabled={!user}
        />
        <button type="submit" disabled={!user}>Post</button>
      </form>
    </div>
  );
};

const CommentItem = ({ comment, onReply, onLike, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (user) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  return (
    <div className="comment">
      <div className="comment-avatar" style={{ backgroundImage: `url(${comment.avatar})` }}></div>
      <div className="comment-content">
        <strong>{comment.author}</strong> <span>{comment.time}</span>
        <p>{comment.content}</p>
        <div className="comment-actions">
          <button onClick={() => onLike(comment.id)} disabled={!user}>Like {comment.likes}</button>
          <button onClick={() => setShowReplyForm(!showReplyForm)} disabled={!user}>Reply</button>
        </div>
        {showReplyForm && (
          <form onSubmit={handleReplySubmit}>
            <input
              type="text"
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              placeholder="Write a reply..."
            />
            <button type="submit">Post</button>
          </form>
        )}
        {comment.replies && comment.replies.length > 0 && (
          <div className="replies">
            {comment.replies.map((reply) => (
              <CommentItem key={reply.id} comment={reply} onReply={onReply} onLike={onLike} user={user} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentSection;
