import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import DOMPurify from 'dompurify';
import './css/PostPage.css';

const PostPage = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch(`http://localhost:5000/api/posts/${id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setPost(data))
      .catch(error => setError(error.message));
  }, [id]);

  if (error) return <div className="error-message">{error}</div>;
  if (!post) return <div className="loading-message">Loading...</div>;

  const tableOfContents = [
    "Organize Your Contacts",
    "Master the Art of Group Chats",
    "Utilize Multimedia Features",
    "Be Mindful of Privacy Settings",
    "Customize Notifications",
    "Embrace Emojis and GIFs",
    "Backup Your Conversations",
    "Stay Informed About Updates",
    "Practice Digital Etiquette"
  ];

  return (
    <div className="post-page">
      <Link to="/" className="back-link">‚Üê Back</Link>
      <article className="post-content">
        <header className="post-header">
          <span className="post-category">Features</span>
          <h1>{post.title}</h1>
          <p className="post-meta">
            {new Date(post.createdAt).toLocaleString('en-US', { 
              year: 'numeric', 
              month: 'long', 
              day: 'numeric',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </p>
        </header>
        {post.imageUrl && (
          <div className="post-image-container">
            <img src={post.imageUrl} alt={post.title} className="post-image" />
          </div>
        )}
        <div className="post-body">
          <nav className="table-of-contents">
            <ul>
              {tableOfContents.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </nav>
          <div
            dangerouslySetInnerHTML={{
              __html: DOMPurify.sanitize(post.content)
            }}
            className="post-text-content"
          />
        </div>
        <footer className="post-footer">
          <h3>Share Article</h3>
          <div className="share-buttons">
            <button className="share-button instagram">Instagram</button>
            <button className="share-button linkedin">LinkedIn</button>
            <button className="share-button tiktok">TikTok</button>
          </div>
        </footer>
      </article>
    </div>
  );
};

export default PostPage;
