import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import CommentSection from './CommentSection';
import DOMPurify from 'dompurify';
import './css/PostPage.css';

const PostPage = (user) => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [recentPosts, setRecentPosts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    console.log("Fetching post data...");

    // Fetch current post
    fetch(`http://localhost:5000/api/posts/${id}`)
      .then(response => {
        if (!response.ok) {
          return response.json().then(error => { throw new Error(error.error) });
        }
        return response.json();
      })
      .then(data => setPost(data))
      .catch(error => setError(error.message));

    // Fetch recent posts
    fetch('http://localhost:5000/api/posts?limit=5')
      .then(response => response.json())
      .then(data => setRecentPosts(data))
      .catch(error => console.error('Error fetching recent posts:', error));
  }, [id]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!post) {
    return <div>Loading...</div>;
  }

  return (
    <div className="post-page">
      <article className="post-content">
        <header className="post-header">
          <h1>{post.title}</h1>
          <p className="post-meta">
            <span className="read-count">조회수: {post.readCount}</span>
            <span className="post-date">작성일: {new Date(post.createdAt).toLocaleDateString()}</span>
          </p>
        </header>
        {post.imageUrl && (
          <div className="post-image-container">
            <img src={post.imageUrl} alt={post.title} className="post-image" />
          </div>
        )}
        <div 
          dangerouslySetInnerHTML={{
            __html: DOMPurify.sanitize(post.content)
          }}
          className="post-text-content"
        />
      </article>
      <CommentSection postId={id} user={user}/>
      <aside className="recent-posts">
        <h3>최근 게시물</h3>
        <ul>
          {recentPosts.slice(0, 5).map(recentPost => (
            <li key={recentPost.id} className={recentPost.id === post.id ? 'current-post' : ''}>
              <Link to={`/post/${recentPost.id}`}>{recentPost.title}</Link>
            </li>
          ))}
        </ul>
        {recentPosts.length > 5 && (
          <Link to="/posts" className="more-posts">더 보기</Link>
        )}
      </aside>
    </div>
  );
};

export default PostPage;
