import React from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Header.css';

const Header = ({ toggleSidebar, user, onLogout }) => {
  const navigate = useNavigate();

  return (
    <header className="header">
      <div className="header-left">
        <button className="sidebar-toggle-btn" onClick={toggleSidebar}>â˜°</button>
        <div className="logo">
          <h2>Cloud Blog</h2>
        </div>
      </div>
      <nav className="header-nav">
        <a href="/product" className="nav-link">Product</a>
        <a href="/download" className="nav-link">Download</a>
        <a href="/solutions" className="nav-link">Solutions</a>
        <a href="/resources" className="nav-link">Resources</a>
        <a href="/pricing" className="nav-link">Pricing</a>
      </nav>
      <div className="header-right">
        {user ? (
          <>
            <span className="username">{user.username}</span>
            <button onClick={onLogout} className="button button-secondary">Log out</button>
          </>
        ) : (
          <>
            <button className="button button-secondary" onClick={() => navigate('/login')}>Log in</button>
            <button className="button button-primary">Get Cloud Blog free</button>
          </>
        )}
      </div>
    </header>
  );
};

export default Header;
