import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import CategoryTag from './CategoryTag';
import PostList from './PostList';
import './css/HomePage.css';

const HomePage = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const categories = [
    "Kubernetes", "Serverless", "DevOps", "AWS", "Azure",
    "Google Cloud", "Docker", "Python", "Microservices",
    "CI/CD", "Terraform", "Machine Learning"
  ];

  return (
    <div className="home-page">
      <Header toggleSidebar={toggleSidebar} />
      <div className="content-wrapper">
        <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
        <main className={`main-content ${isSidebarOpen ? 'shifted' : ''}`}>
          <div className="explore-section">
            <h1 className="explore-title">Tools & Cloud</h1>
            <p className="explore-subtitle">Thoughts on the future of cloud technology, from the people and teams creating it.</p>
          </div>

          <div className="content-grid">
            <div className="categories-sidebar">
              <h2 className="categories-title">Categories</h2>
              <ul className="categories-list">
                {categories.map((category, index) => (
                  <li key={index} className="category-item">
                    <CategoryTag category={category} />
                  </li>
                ))}
              </ul>
            </div>

            <div className="posts-section">
              <PostList />
            </div>
          </div>

          <div className="marketing-section">
            <div className="marketing-card">
              <h3 className="marketing-title">Marketing Home</h3>
              <p>Have a marketing request?</p>
              <div className="marketing-actions">
                <button className="marketing-button">Social media request</button>
                <button className="marketing-button">Email request</button>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default HomePage;
