import React, { useState, useEffect } from 'react';
import './css/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');

  // Fetch all posts or filtered posts based on the searchTerm
  const fetchPosts = async (query = '') => {
    try {
      let url = 'http://localhost:5000/api/posts';
      if (query.length > 2) {
        url += `/search?searchTerm=${encodeURIComponent(query)}`;
      }

      const response = await fetch(url);
      const data = await response.json();

      if (Array.isArray(data)) {
        setPosts(data); // Set posts to display in the list
      } else {
        setPosts([]);
      }
    } catch (error) {
      console.error('Error fetching posts:', error);
      setPosts([]);
    }
  };

  // Handle input change and fetch posts
  const handleInputChange = (e) => {
    const query = e.target.value;
    setSearchTerm(query);

    // If the query is empty, fetch all posts
    if (query.length === 0) {
      setError('');
      fetchPosts(); // Fetch all posts if input is empty
    } else if (query.length > 2) {
      setError('');
      fetchPosts(query); // Fetch filtered posts when input is valid
    } else {
      setPosts([]); // Clear post list if input is too short
      setError(''); // No error message when fewer than 2 characters are typed
    }
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.length > 2) {
      onSearch(searchTerm);
      fetchPosts(searchTerm); // Trigger the search function
    }
  };

  // Fetch all posts on component load
  useEffect(() => {
    fetchPosts(); // Fetch all posts on initial load
  }, []);

  return (
    <div className="search-container">
      <h2 className="search-title">FIND</h2>
      <form onSubmit={handleSubmit} className="search-bar">
        <input
          type="text"
          placeholder="Search posts..."
          value={searchTerm}
          onChange={handleInputChange}
          className="search-input"
        />
        <button type="submit" className="search-button">
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <circle cx="11" cy="11" r="8"></circle>
            <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
          </svg>
        </button>
      </form>

      {/* Display posts in a list */}
      <ul className="posts-list">
        {error && <li className="error-message">{error}</li>}
        {posts.length > 0 && posts.map((post, index) => (
          <li key={index} className="post-item">
            <h3 className="post-title">{post.title}</h3>
            <p>{new Date(post.createdAt).toLocaleString()}</p>
            <span>{post.readCount || 0} views</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchBar;
