import React, { useState, useEffect, useCallback } from 'react';
import io from 'socket.io-client';
import CommentItem from './CommentItem';
import './css/Comment.css';

const socket = io('http://localhost:5000');

const CommentSection = ({ postId, user }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const userId = user?.userId || null;

  const fetchComments = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/comments/${postId}?userId=${userId}`);
      const data = await response.json();

      const processedComments = data.map(comment => ({
        ...comment,
        likedUsers: Array.isArray(comment.likedUsers) ? comment.likedUsers : [],
        isLikedByCurrentUser: comment.isLikedByCurrentUser,
        likeCount: comment.likeCount || 0,
        username: comment.user.username,
        replies: Array.isArray(comment.replies) ? comment.replies.map(reply => ({
          ...reply,
          likedUsers: Array.isArray(reply.likedUsers) ? reply.likedUsers : [],
          isLikedByCurrentUser: reply.isLikedByCurrentUser,
          likeCount: reply.likeCount || 0,
          username: reply.username
        })) : []
      }));
      setComments(processedComments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  }, [postId, userId]);

  const handleDelete = async (commentId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/comments/${commentId}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId })
      });

      if (response.ok) {
        setComments(prevComments => prevComments.filter(comment => comment.id !== commentId));
      } else {
        console.error('Failed to delete the comment');
      }
    } catch (error) {
      console.error('Error deleting comment:', error);
    }
  };

  useEffect(() => {
    fetchComments();

    socket.emit('joinPost', postId);

    socket.on('newComment', (comment) => {
      setComments((prevComments) => {
        // If the comment already exists (by id or tempId), don't add it
        const commentExists = prevComments.some(c => c.id === comment.id || c.id === comment.tempId);
        if (commentExists) {
          return prevComments.map(c => 
            (c.id === comment.tempId || c.id === comment.id) ? { ...comment, id: comment.id } : c
          );
        }

        // Handle new reply
        if (comment.parentId) {
          return prevComments.map(parentComment =>
            parentComment.id === comment.parentId
              ? { ...parentComment, replies: [...parentComment.replies, comment] }
              : parentComment
          );
        }

        // Handle new root-level comment
        return [...prevComments, comment];
      });
    });

    socket.on('updateLike', (updatedComment) => {
      setComments((prevComments) => {
        return prevComments.map((comment) => {
          if (comment.id === updatedComment.commentId) {
            return { ...comment, likeCount: updatedComment.likeCount ?? 0, isLikedByCurrentUser: updatedComment.isLikedByCurrentUser };
          }

          const updatedReplies = comment.replies.map((reply) =>
            reply.id === updatedComment.commentId 
              ? { ...reply, likeCount: updatedComment.likeCount ?? 0, isLikedByCurrentUser: updatedComment.isLikedByCurrentUser } 
              : reply
          );
          return { ...comment, replies: updatedReplies };
        });
      });
    });

    return () => {
      socket.emit('leavePost', postId);
      socket.off('newComment');
      socket.off('updateLike');
    };
  }, [postId, userId, fetchComments]);

  const handleCommentSubmit = (e, parentId = null) => {
    e.preventDefault();

    if (newComment.trim()) {
      const tempId = 'temp-' + Math.random().toString(36).substr(2, 9);

      const newCommentData = {
        id: tempId,
        content: newComment,
        userId,
        username: user.username,
        likeCount: 0,
        likedUsers: [],
        isLikedByCurrentUser: false,
        replies: [],
        createdAt: new Date().toISOString(),
      };

      // Only update the state if it's a reply, otherwise wait for the server response
      if (parentId) {
        setComments(prevComments => 
          prevComments.map(comment => 
            comment.id === parentId
              ? { ...comment, replies: [...comment.replies, newCommentData] }
              : comment
          )
        );
      }

      socket.emit('createComment', {
        postId,
        content: newComment,
        parentId: parentId || null,
        userId,
        tempId
      });

      setNewComment('');
    }
  };

  const handleReply = (parentId, replyText) => {
    handleCommentSubmit({ preventDefault: () => {} }, parentId);
  };

  const handleLike = (commentId, isLiked) => {
    if (!userId) {
      alert('Please log in to like a comment.');
      return;
    }

    socket.emit('likeComment', { commentId, isLiked, userId });

    setComments((prevComments) =>
      prevComments.map((comment) => {
        if (comment.id === commentId) {
          return {
            ...comment,
            likeCount: isLiked ? comment.likeCount - 1 : comment.likeCount + 1,
            isLikedByCurrentUser: !isLiked
          };
        }

        const updatedReplies = comment.replies.map((reply) =>
          reply.id === commentId
            ? {
                ...reply,
                likeCount: isLiked ? reply.likeCount - 1 : reply.likeCount + 1,
                isLikedByCurrentUser: !isLiked
              }
            : reply
        );
        return { ...comment, replies: updatedReplies };
      })
    );
  };

  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <ul>
        {comments.length > 0 ? (
          comments.map((comment) => (
            <CommentItem
              key={comment.id}
              comment={comment}
              onReply={handleReply}
              onLike={handleLike}
              user={user}
            />
          ))
        ) : (
          <p>No comments available.</p>
        )}
      </ul>
      <form onSubmit={(e) => handleCommentSubmit(e)}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
          disabled={!userId}
        />
        <button type="submit" disabled={!userId}>Post</button>
      </form>
    </div>
  );
};

export default CommentSection;
