import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import CommentSection from './CommentSection';
import DOMPurify from 'dompurify';
import './css/PostPage.css';

const PostPage = ({ user }) => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [recentPosts, setRecentPosts] = useState([]);
  const [error, setError] = useState('');
  const [loadingMore, setLoadingMore] = useState(false);
  const [page, setPage] = useState(1);
  const navigate = useNavigate();
  const limit = 5;

  useEffect(() => {
    // Fetch current post
    fetch(`http://localhost:5000/api/posts/${id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setPost(data))
      .catch(error => setError(error.message));

    // Fetch recent posts, limit to 5 and include pagination
    fetchRecentPosts(0); // Load first 5 posts on mount
  }, [id]);

  const fetchRecentPosts = (offset) => {
    fetch(`http://localhost:5000/api/posts?limit=${limit}&offset=${offset}`)
      .then(response => response.json())
      .then(data => {
        setRecentPosts((prevPosts) => [...prevPosts, ...data]);
        setLoadingMore(false);
      })
      .catch(error => console.error('Error fetching recent posts:', error));
  };

  const handleLoadMore = () => {
    setLoadingMore(true);
    const nextOffset = page * limit;
    fetchRecentPosts(nextOffset);
    setPage((prevPage) => prevPage + 1);
  };

  if (error) return <div className="error-message">{error}</div>;
  if (!post) return <div className="loading-message">Loading...</div>;

  return (
    <div className="post-page-container">
      <div className="back-arrow" onClick={() => navigate('/')}>
        ← Back
      </div>

      <div className="post-page">
        <aside className="sidebar">
          <h2 className="sidebar-title">Recent Posts</h2>
          <ul className="recent-posts-list">
            {recentPosts.map(recentPost => (
              <li 
                key={recentPost.id} 
                className={recentPost.id === post.id ? 'active recent-post-item' : 'recent-post-item'}
              >
                <Link to={`/post/${recentPost.id}`} className="recent-post-link">
                  {recentPost.title}
                </Link>
              </li>
            ))}
          </ul>
          {recentPosts.length >= limit * page && (
            <button onClick={handleLoadMore} disabled={loadingMore}>
              {loadingMore ? 'Loading...' : 'Show More'}
            </button>
          )}
        </aside>

        <div className="post-content">
          <div className="post-category">
            {post.category && <p>{post.category}</p>}
          </div>
          <h1 className="post-title">{post.title}</h1>
          <p className="post-date">
            {new Date(post.createdAt).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </p>
          <p className="view-count">👁 {post.readCount || 0} views</p>
          {post.imageUrl && (
            <div className="post-image-container">
              <img src={post.imageUrl} alt={post.title} className="post-image" />
            </div>
          )}
          <div
            dangerouslySetInnerHTML={{
              __html: DOMPurify.sanitize(post.content)
            }}
            className="post-text-content"
          />
          <CommentSection postId={id} user={user} />
        </div>
      </div>
    </div>
  );
};

export default PostPage;
