import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp as solidThumbsUp } from '@fortawesome/free-solid-svg-icons';
import './css/CommentItem.css';

const CommentItem = ({ comment, onReply, onLike, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');
  const [isLiked, setIsLiked] = useState(false);

  const userId = user?.user?.userId || null;

  useEffect(() => {
    if (userId && comment.likedUsers && comment.likedUsers.includes(userId)) {
      setIsLiked(true);
    }
  }, [userId, comment.likedUsers]);

  const handleLikeClick = () => {
    if (userId) {
      onLike(comment.id, isLiked);
      setIsLiked(!isLiked); // Toggle the like state
    } else {
      alert('Please log in to like this comment.');
    }
  };

  const likeButtonClass = isLiked ? 'like-button liked' : 'like-button';

  return (
    <div className="comment">
      <div className="comment-content">
        <strong>{comment.author}</strong>
        <p>{comment.content}</p>
        <div className="comment-actions">
          <span className={likeButtonClass} onClick={handleLikeClick}>
            <FontAwesomeIcon icon={solidThumbsUp} /> {comment.likes}
          </span>
          <button onClick={() => setShowReplyForm(!showReplyForm)} disabled={!userId}>
            Reply
          </button>
        </div>
        {showReplyForm && (
          <form onSubmit={(e) => {
            e.preventDefault();
            onReply(comment.id, replyText);
            setReplyText('');
            setShowReplyForm(false);
          }}>
            <input
              type="text"
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              placeholder="Write a reply..."
            />
            <button type="submit">Post</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
