import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp as solidThumbsUp } from '@fortawesome/free-solid-svg-icons';
import './css/CommentItem.css';

const CommentItem = ({ comment, onReply, onLike, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');
  const [isLiked, setIsLiked] = useState(false);
  const [likes, setLikes] = useState(comment.likes || 0); // Initialize with the current number of likes

  const userId = user?.user?.userId || null;

  useEffect(() => {
    socket.on('updateLike', ({ commentId, likesCount }) => {
      setComments((prevComments) =>
        prevComments.map((comment) =>
          comment.id === commentId ? { ...comment, likes: likesCount } : comment
        )
      );
    });

    return () => {
      socket.off('updateLike');
    };
  }, [socket]);


  const handleLikeClick = () => {
    if (userId) {
      // Only allow the like action if the user has not already liked the comment
      if (isLiked) {
        // User has already liked the comment, so unlike it
        onLike(comment.id, isLiked);
        setLikes(prevLikes => prevLikes - 1); // Decrease likes count
      } else {
        // User has not liked the comment, so like it
        onLike(comment.id, isLiked);
        setLikes(prevLikes => prevLikes + 1); // Increase likes count
      }
      setIsLiked(!isLiked); // Toggle like status
    } else {
      alert('Please log in to like this comment.');
    }
  };

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (userId) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  const avatarInitial = comment.author ? comment.author.charAt(0).toUpperCase() : '?';

  return (
    <div className="comment">
      <div className="comment-avatar">
        <span className="avatar-initial">{avatarInitial}</span>
      </div>
      <div className="comment-content">
        <strong>{comment.author}</strong> <span>{comment.time}</span>
        <p>{comment.content}</p>
        <div className="comment-actions">
          <span
            className={`like-button ${isLiked ? 'liked' : ''}`}
            onClick={handleLikeClick}
          >
            <FontAwesomeIcon icon={solidThumbsUp} /> {likes} {/* Display the updated likes */}
          </span>

          {comment.parentId === null && (
            <button onClick={() => setShowReplyForm(!showReplyForm)} disabled={!userId}>
              Reply
            </button>
          )}
        </div>

        {showReplyForm && (
          <form onSubmit={handleReplySubmit}>
            <input
              type="text"
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              placeholder="Write a reply..."
            />
            <button type="submit">Post</button>
          </form>
        )}

        {comment.replies && comment.replies.length > 0 && (
          <div className="replies">
            {comment.replies.map((reply) => (
              <CommentItem
                key={reply.id}
                comment={reply}
                onReply={onReply}
                onLike={onLike}
                user={user}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
