import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import CategoryTag from './CategoryTag';
import PostList from './PostList';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import './css/HomePage.css';

const HomePage = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const categories = [
    "Kubernetes", "Serverless", "DevOps", "AWS", "Azure",
    "Google Cloud", "Docker", "Python", "Microservices",
    "CI/CD", "Terraform", "Machine Learning"
  ];

  return (
    <div className="home-page">
      <Header toggleSidebar={toggleSidebar} />
      <div className="flex">
        <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
        <main className={`main-content ${isSidebarOpen ? 'shifted' : ''} p-6 flex-grow`}>
          <div className="max-w-6xl mx-auto">
            <div className="mb-8">
              <h1 className="text-4xl font-bold mb-2">Tools & Cloud</h1>
              <p className="text-xl text-gray-600">Thoughts on the future of cloud technology, from the people and teams creating it.</p>
            </div>

            <div className="grid grid-cols-4 gap-6">
              <div className="col-span-1">
                <h2 className="text-lg font-semibold mb-4">Categories</h2>
                <ul className="space-y-2">
                  {categories.map((category, index) => (
                    <li key={index} className="text-blue-600 hover:underline cursor-pointer">
                      <CategoryTag category={category} />
                    </li>
                  ))}
                </ul>
              </div>

              <div className="col-span-3">
                <PostList />
              </div>
            </div>

            <div className="mt-8">
              <Card className="w-1/3">
                <CardHeader>
                  <h3 className="text-xl font-semibold">Marketing Home</h3>
                </CardHeader>
                <CardContent>
                  <p>Have a marketing request?</p>
                  <div className="mt-4 space-y-2">
                    <button className="text-blue-600 hover:underline">Social media request</button>
                    <button className="text-blue-600 hover:underline">Email request</button>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default HomePage;
