import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './css/AdminPage.css';

const AdminPage = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('Drafts');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` // Assuming you store the token in localStorage
        }
      });
      if (!response.ok) {
        throw new Error(await response.text());
      }
      const data = await response.json();
      setPosts(data);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleNewPost = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          title: 'New Post',
          content: 'Start writing your post here...',
          author: 'Admin', // You might want to get this from the user's session
          status: 'Drafts'
        })
      });
      if (!response.ok) {
        throw new Error(await response.text());
      }
      const newPost = await response.json();
      setPosts([...posts, newPost]);
    } catch (error) {
      setError(error.message);
    }
  };

  // Filter posts based on active tab and search term
  const filteredPosts = posts
    .filter((post) => post.status === activeTab)
    .filter((post) => post.title.toLowerCase().includes(searchTerm.toLowerCase()));

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="admin-page">
      <div className="header">
        <h1>Posts</h1>
        <button className="new-post-button" onClick={handleNewPost}>New Post</button>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search posts"
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      <div className="tabs">
        {['Drafts', 'Published', 'Scheduled'].map((tab) => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => handleTabClick(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="post-list">
        {filteredPosts.length > 0 ? (
          filteredPosts.map((post) => (
            <div key={post.id} className="post-item">
              <div
                className="post-thumbnail"
                style={{ backgroundImage: `url(${post.thumbnailUrl || '/default-thumbnail.png'})` }}
              ></div>
              <div className="post-content">
                <h3>{post.title}</h3>
                <p>{new Date(post.createdAt).toLocaleString()}</p>
                <p>{post.views} views, {post.likes} likes</p>
                <Link to={`/post/${post.id}`}>
                  <button>Edit</button>
                </Link>
              </div>
            </div>
          ))
        ) : (
          <p>No posts found for {activeTab}.</p>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
