import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp } from '@fortawesome/free-solid-svg-icons';

const CommentItem = ({ comment, onReply, onLike, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');
  const [isLiked, setIsLiked] = useState(false);

  useEffect(() => {
    // Check if the user has liked this comment
    if (user && comment.likedUsers && comment.likedUsers.includes(user.userId)) {
      setIsLiked(true);
    }
  }, [user, comment.likedUsers]);

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (user) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  const handleLikeClick = () => {
    if (user) {
      onLike(comment.id, isLiked);
      setIsLiked(!isLiked); // Toggle like status
    } else {
      alert('Please log in to like this comment.');
    }
  };

  return (
    <div className="comment">
      <div className="comment-avatar" style={{ backgroundImage: `url(${comment.avatar})` }}></div>
      <div className="comment-content">
        <strong>{comment.author}</strong> <span>{comment.time}</span>
        <p>{comment.content}</p>
        <div className="comment-actions">
          <button onClick={handleLikeClick} disabled={!user}>
            <FontAwesomeIcon
              icon={faThumbsUp}
              style={{ color: isLiked ? 'blue' : 'gray' }} // Change color based on like status
            />{' '}
            {comment.likes}
          </button>
          <button onClick={() => setShowReplyForm(!showReplyForm)} disabled={!user}>
            Reply
          </button>
        </div>
        {showReplyForm && (
          <form onSubmit={handleReplySubmit}>
            <input
              type="text"
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              placeholder="Write a reply..."
            />
            <button type="submit">Post</button>
          </form>
        )}
        {comment.replies && comment.replies.length > 0 && (
          <div className="replies">
            {comment.replies.map((reply) => (
              <CommentItem key={reply.id} comment={reply} onReply={onReply} onLike={onLike} user={user} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
