import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/PostList.css';

const PostList = (user) => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Fetched posts:', data);
      const publishedPosts = data.filter(post => post.status === 'Published');
      setPosts(publishedPosts);
    } catch (error) {
      console.error('Error fetching posts:', error);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleReadMoreClick = (postId, postTitle) => {
    console.log(`Navigating to post ID: ${postId}, Title: ${postTitle}`);
    navigate(`/post/${postId}`);
  };

  if (isLoading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="post-list">
      {posts.length === 0 ? (
        <p className="no-posts">No posts found.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} className="post-card">
            {post.image && (
              <div className="post-image">
                <img src={post.image} alt={post.title} />
              </div>
            )}
            <div className="post-content">
              <h3 className="post-title">{post.title}</h3>
              <p className="post-date">{new Date(post.createdAt).toLocaleString()}</p>
              {post.excerpt && <p className="post-excerpt">{post.excerpt}</p>}
              <div className="post-footer">
                <span className="view-count">üëÅ {post.readCount || 0} views</span>
                <button 
                  className="read-more-btn"
                  onClick={() => handleReadMoreClick(post.id, post.title)}
                >
                  Read More
                </button>
              </div>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
