import React, { useState, useEffect } from 'react';
import './css/AdminPage.css';

const AdminPage = () => {
  const [posts, setPosts] = useState([]);
  const [activeTab, setActiveTab] = useState('Drafts'); // To handle the tabs
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('/api/admin/posts');
      const data = await response.json();
      setPosts(data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredPosts = posts.filter((post) =>
    post.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="admin-page">
      <div className="header">
        <h1>Posts</h1>
        <button className="new-post-button">New Post</button>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search posts"
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      <div className="tabs">
        {['Drafts', 'Published', 'Scheduled', 'Unlisted'].map((tab) => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => handleTabClick(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <ul className="post-list">
        {filteredPosts.map((post) => (
          <li key={post.id} className="post-item">
            <div className="post-thumbnail">
              <img src={post.thumbnailUrl || '/default-thumbnail.png'} alt={post.title} />
            </div>
            <div className="post-info">
              <h3>{post.title}</h3>
              <p>{new Date(post.createdAt).toDateString()} Â· {post.views} views, {post.likes} likes</p>
            </div>
            <div className="post-menu">
              <button className="menu-button">...</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPage;
