import React, { useState, useEffect } from 'react';
import Header from './Header';
import CategoryTag from './CategoryTag';
import PostList from './PostList';
import './css/HomePage.css';

const HomePage = ({ user, onLogout }) => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null); // Track selected category

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/categories');
        if (!response.ok) {
          throw new Error('Failed to fetch categories');
        }
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  // Function to handle category click
  const handleCategoryClick = (category) => {
    setSelectedCategory(category);  // Update selected category
  };

  return (
    <div className="home-page">
      <div className="content-wrapper">
        <aside className="home_sidebar">
          <h1 className="page-title">Tools & Cloud</h1>
          <p className="page-subtitle">Thoughts on the future of cloud technology, from the people and teams creating it.</p>
          <nav className="sidebar-nav">
            <h2>Latest</h2>
            <ul>
              <li>Cloud HQ</li>
              <li>For Teams</li>
              <li>Tech</li>
              <li>Inspiration</li>
              <li>Podcast</li>
            </ul>
          </nav>
        </aside>
        <main className="main-content">
          <section className="posts-section">
            <PostList selectedCategory={selectedCategory} />  {/* Pass selected category */}
          </section>
        </main>
        <aside className="categories-sidebar">
          <div className="categories-container">
            <h2 className="categories-title">Categories</h2>
            <div className="categories-list">
              {categories.map((category, index) => (
                <CategoryTag 
                  key={index} 
                  category={category} 
                  onClick={() => handleCategoryClick(category)}  // Set the selected category on click
                />
              ))}
            </div>
          </div>
          <div className="marketing-card">
            <h3 className="marketing-title">Marketing Home</h3>
            <p>Have a marketing request?</p>
            <a href="/social-media-request" className="marketing-link">Social media request</a>
            <a href="/email-request" className="marketing-link">Email request</a>
          </div>
        </aside>
      </div>
    </div>
  );
};

export default HomePage;
