import React, { useState } from 'react';
import './css/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [activeTab, setActiveTab] = useState('Rooms');
  const [location, setLocation] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [guests, setGuests] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch({ activeTab, location, checkIn, checkOut, guests });
  };

  return (
    <div className="complex-search-bar">
      <h2 className="search-title">FIND</h2>
      <div className="search-tabs">
        {['Rooms', 'Flats', 'Hostels', 'Villas'].map(tab => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>
      <form onSubmit={handleSubmit} className="search-form">
        <input
          type="text"
          placeholder="Which city do you prefer?"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="search-input location"
        />
        <input
          type="text"
          placeholder="Add Dates"
          value={checkIn}
          onChange={(e) => setCheckIn(e.target.value)}
          className="search-input check-in"
        />
        <input
          type="text"
          placeholder="Add Dates"
          value={checkOut}
          onChange={(e) => setCheckOut(e.target.value)}
          className="search-input check-out"
        />
        <input
          type="text"
          placeholder="Add Guests"
          value={guests}
          onChange={(e) => setGuests(e.target.value)}
          className="search-input guests"
        />
        <button type="submit" className="search-button">
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <circle cx="11" cy="11" r="8"></circle>
            <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
          </svg>
        </button>
      </form>
    </div>
  );
};

export default SearchBar;
