import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp as solidThumbsUp } from '@fortawesome/free-solid-svg-icons';
import './css/CommentItem.css';

const CommentItem = ({ comment, onReply, onLike, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');
  const userId = user?.userId || null;

  // Check if the current user has liked this comment
  const isLikedByCurrentUser = comment.likedUsers.includes(userId);

  const handleLikeClick = () => {
    if (userId) {
      onLike(comment.id, isLikedByCurrentUser); // Pass the current like status
    } else {
      alert('Please log in to like this comment.');
    }
  };

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (userId) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  // Get the first letter of the author's name for the avatar
  const avatarInitial = typeof comment.userId === 'string'
  ? comment.userId.charAt(0).toUpperCase()
  : comment.author ? comment.author.charAt(0).toUpperCase() : '?';


  // Generate a background color for the avatar based on the author's name
  const generateColor = (input) => {
    const colors = ['#D8E0BB', '#B6CEC7', '#86A3C3', '#7268A6', '#6B3074'];
    const index = input ? input.charCodeAt(0) % colors.length : 0;
    return colors[index];
  };

  return (
    <div className="comment">
      <div
        className="comment-avatar"
        style={{ backgroundColor: generateColor(comment.userId) }}
      >
        <span className="avatar-initial">{avatarInitial}</span>
      </div>
      <div className="comment-content">
        <strong>{comment.userId}</strong> <span>{comment.time}</span>
        <p>{comment.content}</p>
        <div className="comment-actions">
          <span
            className={`like-button ${isLikedByCurrentUser ? 'liked' : ''}`}
            onClick={handleLikeClick}
          >
            <FontAwesomeIcon icon={solidThumbsUp} /> {comment.likeCount || 0}
          </span>
          {comment.parentId === null && (
            <span className="reply-text" onClick={() => setShowReplyForm(!showReplyForm)} disabled={!userId}>
              Reply
            </span>
          )}
        </div>

        {showReplyForm && (
          <form onSubmit={handleReplySubmit} className="reply-form">
            <input
              type="text"
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              placeholder="Write a reply..."
            />
            <button type="submit">Post</button>
          </form>
        )}

        {comment.replies && comment.replies.length > 0 && (
          <div className="replies">
            {comment.replies.map((reply) => (
              <CommentItem
                key={reply.id}
                comment={reply}
                onReply={onReply}
                onLike={onLike}
                user={user}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
