import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import CommentItem from './CommentItem';
import './css/CommentSection.css';

const socket = io('http://localhost:5000'); // Update with your backend URL

const CommentSection = ({ postId, user }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  const userId = user?.user?.userId || null;

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/comments/${postId}`);
        const data = await response.json();

        const commentsWithLikes = await Promise.all(data.map(async (comment) => {
          try {
            const likeResponse = await fetch(`http://localhost:5000/api/comments/${comment.id}/likes`);
            if (!likeResponse.ok) {
              throw new Error('Failed to fetch likes');
            }
            const likesData = await likeResponse.json();
            return { ...comment, likes: likesData.count };
          } catch (error) {
            console.error(`Error fetching likes for comment ${comment.id}:`, error);
            return { ...comment, likes: 0 }; // Default to 0 if an error occurs
          }
        }));

        setComments(commentsWithLikes);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };


    fetchComments();

    socket.emit('joinPost', postId);

    socket.on('newComment', (comment) => {
      comment.likedUsers = Array.isArray(comment.likedUsers) ? comment.likedUsers : [];

      setComments((prevComments) => {
        if (comment.parentId) {
          return prevComments.map(parentComment =>
            parentComment.id === comment.parentId
              ? { ...parentComment, replies: [...parentComment.replies, comment] }
              : parentComment
          );
        }
        return [...prevComments, comment];
      });
    });

    socket.on('updateLike', (updatedComment) => {
      setComments((prevComments) => {
        return prevComments.map(comment => {
          if (comment.id === updatedComment.id) {
            return updatedComment;
          }

          const updatedReplies = comment.replies.map(reply =>
            reply.id === updatedComment.id ? updatedComment : reply
          );
          return { ...comment, replies: updatedReplies };
        });
      });
    });

    return () => {
      socket.emit('leavePost', postId);
      socket.off('newComment');
      socket.off('updateLike');
    };
  }, [postId]);

  const handleCommentSubmit = (e, parentId = null) => {
    e.preventDefault();

    if (newComment.trim()) {
      socket.emit('createComment', {
        postId,
        content: newComment,
        parentId: parentId || null,
        userId
      });

      setNewComment('');
    }
  };

  const handleReply = (parentId, replyText) => {
    if (replyText.trim()) {
      socket.emit('createComment', {
        postId,
        content: replyText,
        parentId,
        userId
      });
    }
  };

  const handleLike = (commentId, isLiked) => {
    if (!userId) {
      alert('Please log in to like a comment.');
      return;
    }

    console.log(`Toggling like for comment ${commentId}, isLiked: ${isLiked}`);

    socket.emit('likeComment', { commentId, isLiked, userId });

    setComments((prevComments) =>
      prevComments.map((comment) => {
        if (comment.id === commentId) {
          const updatedLikes = isLiked ? comment.likes - 1 : comment.likes + 1;
          const updatedLikedUsers = isLiked
            ? comment.likedUsers.filter((id) => id !== userId)
            : [...comment.likedUsers, userId];

          console.log(`Updated likes: ${updatedLikes}, likedUsers: ${updatedLikedUsers}`);

          return {
            ...comment,
            likes: updatedLikes,
            likedUsers: updatedLikedUsers,
          };
        }

        const updatedReplies = comment.replies.map((reply) =>
          reply.id === commentId
            ? {
                ...reply,
                likes: isLiked ? reply.likes - 1 : reply.likes + 1,
                likedUsers: isLiked
                  ? reply.likedUsers.filter((id) => id !== userId)
                  : [...reply.likedUsers, userId],
              }
            : reply
        );
        return { ...comment, replies: updatedReplies };
      })
    );
  };

  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <ul>
        {comments.length > 0 ? (
          comments.map((comment, index) => (
            <CommentItem
              key={index}
              comment={comment}
              onReply={handleReply}
              onLike={handleLike}
              user={user}
            />
          ))
        ) : (
          <p>No comments available.</p>
        )}
      </ul>
      <form onSubmit={handleCommentSubmit}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
          disabled={!userId}
        />
        <button type="submit" disabled={!userId}>Post</button>
      </form>
    </div>
  );
};

export default CommentSection;
