import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import CommentItem from './CommentItem';
import './css/Comment.css';

const socket = io('http://localhost:5000');

const CommentSection = ({ postId, user }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const userId = user?.userId || null;

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/comments/${postId}?userId=${userId}`);
        const data = await response.json();

        const processedComments = data.map(comment => ({
          ...comment,
          likedUsers: Array.isArray(comment.likedUsers) ? comment.likedUsers : [],
          isLikedByCurrentUser: comment.isLikedByCurrentUser,
          likeCount: comment.likeCount || 0,
          username: comment.user.username,
          replies: Array.isArray(comment.replies) ? comment.replies.map(reply => ({
            ...reply,
            likedUsers: Array.isArray(reply.likedUsers) ? reply.likedUsers : [],
            isLikedByCurrentUser: reply.isLikedByCurrentUser,
            likeCount: reply.likeCount || 0,
            username: reply.username
          })) : []
        }));
        setComments(processedComments);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchComments();

    socket.emit('joinPost', postId);

    socket.on('newComment', (comment) => {
      setComments((prevComments) => {
        // Replace optimistic comment with real one if tempId exists
        if (comment.tempId) {
          return prevComments.map(parentComment => {
            if (parentComment.id === comment.tempId) {
              return comment; // Replace optimistic comment with the real one
            }

            if (parentComment.replies && parentComment.replies.some(reply => reply.id === comment.tempId)) {
              return {
                ...parentComment,
                replies: parentComment.replies.map(reply =>
                  reply.id === comment.tempId ? comment : reply
                ),
              };
            }

            return parentComment;
          });
        }

        // Handle new reply
        if (comment.parentId) {
          return prevComments.map(parentComment =>
            parentComment.id === comment.parentId
              ? { ...parentComment, replies: [...parentComment.replies, comment] }
              : parentComment
          );
        }

        // Handle new root-level comment
        return [...prevComments, comment];
      });
    });

    socket.on('updateLike', (updatedComment) => {
      setComments((prevComments) => {
        return prevComments.map((comment) => {
          if (comment.id === updatedComment.commentId) {
            return { ...comment, likeCount: updatedComment.likeCount ?? 0 };
          }

          const updatedReplies = comment.replies.map((reply) =>
            reply.id === updatedComment.commentId ? { ...reply, likeCount: updatedComment.likeCount ?? 0 } : reply
          );
          return { ...comment, replies: updatedReplies };
        });
      });
    });

    return () => {
      socket.emit('leavePost', postId);
      socket.off('newComment');
      socket.off('updateLike');
    };
  }, [postId, userId]);

  const handleCommentSubmit = (e, parentId = null) => {
    e.preventDefault();

    if (newComment.trim()) {
      const tempId = Math.random(); // Temporary ID for the comment/reply

      // Optimistically add the new comment/reply with the current user's username to the state
      setComments(prevComments => {
        const newCommentData = {
          id: tempId, // Temporary ID, will be replaced by the server's ID
          content: newComment,
          userId,
          username: user.username, // Use the logged-in user's username
          likeCount: 0,
          likedUsers: [],
          replies: [],
        };

        if (parentId) {
          return prevComments.map(parentComment =>
            parentComment.id === parentId
              ? { ...parentComment, replies: [...parentComment.replies, newCommentData] }
              : parentComment
          );
        } else {
          return [...prevComments, newCommentData];
        }
      });

      // Emit the new comment to the server via socket
      socket.emit('createComment', {
        postId,
        content: newComment,
        parentId: parentId || null,
        userId
      });

      setNewComment(''); // Clear input field after submission
    }
  };


  const handleReply = (parentId, replyText) => {
    if (replyText.trim()) {
      const tempId = Math.random(); // Temporary ID for the reply

      // Optimistically add the reply to the comment with the current user's username
      setComments(prevComments => {
        const newReplyData = {
          id: tempId, // Temporary ID, will be replaced by the server's ID
          content: replyText,
          userId,
          username: user.username, // Use the logged-in user's username
          likeCount: 0,
          likedUsers: [],
          replies: []
        };

        return prevComments.map(parentComment =>
          parentComment.id === parentId
            ? { ...parentComment, replies: [...parentComment.replies, newReplyData] }
            : parentComment
        );
      });


    }
  };


  const handleLike = (commentId, isLiked) => {
    if (!userId) {
      alert('Please log in to like a comment.');
      return;
    }

    socket.emit('likeComment', { commentId, isLiked, userId });

    setComments((prevComments) =>
      prevComments.map((comment) => {
        if (comment.id === commentId) {
          return {
            ...comment,
            likeCount: isLiked ? comment.likeCount - 1 : comment.likeCount + 1,
            likedUsers: isLiked
              ? comment.likedUsers.filter((id) => id !== userId)
              : [...comment.likedUsers, userId],
            isLikedByCurrentUser: !isLiked
          };
        }

        const updatedReplies = comment.replies.map((reply) =>
          reply.id === commentId
            ? {
                ...reply,
                likeCount: isLiked ? reply.likeCount - 1 : reply.likeCount + 1,
                likedUsers: isLiked
                  ? reply.likedUsers.filter((id) => id !== userId)
                  : [...reply.likedUsers, userId],
                isLikedByCurrentUser: !isLiked
              }
            : reply
        );
        return { ...comment, replies: updatedReplies };
      })
    );
  };

  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <ul>
        {comments.length > 0 ? (
          comments.map((comment, index) => (
            <CommentItem
              key={index}
              comment={comment}
              onReply={handleReply}
              onLike={handleLike}
              user={user}
            />
          ))
        ) : (
          <p>No comments available.</p>
        )}
      </ul>
      <form onSubmit={handleCommentSubmit}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
          disabled={!userId}
        />
        <button type="submit" disabled={!userId}>Post</button>
      </form>
    </div>
  );
};

export default CommentSection;
