import React, { useState, useRef, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp as solidThumbsUp, faTrash, faEllipsisV } from '@fortawesome/free-solid-svg-icons';
import './css/Comment.css';

const CommentItem = ({ comment, onReply, onLike, onDelete, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');
  const [showMenu, setShowMenu] = useState(false);
  const menuRef = useRef(null);
  const userId = user?.userId || null;

  const isLikedByCurrentUser = comment.isLikedByCurrentUser;

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setShowMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLikeClick = () => {
    if (userId) {
      onLike(comment.id, isLikedByCurrentUser);
    } else {
      alert('Please log in to like this comment.');
    }
  };

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (userId) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  const handleDeleteClick = () => {
    if (window.confirm('Are you sure you want to delete this comment?')) {
      onDelete(comment.id);
    }
  };

  const handleMenuToggle = (e) => {
    e.stopPropagation();
    setShowMenu((prevState) => !prevState);
  };

  // ... (rest of the component code remains the same)

  return (
    <div className="comment-container">
      <div className="comment">
        {/* ... (other parts of the component remain unchanged) */}

        {comment.userId === userId && (
          <div className="menu-container" ref={menuRef}>
            <span className="menu-icon" onClick={handleMenuToggle}>
              <FontAwesomeIcon icon={faEllipsisV} />
            </span>
            {showMenu && (
              <div className="dropdown-menu">
                <div className="menu-item" onClick={() => alert('Edit clicked')}>
                  Edit
                </div>
                <div className="menu-item delete" onClick={handleDeleteClick}>
                  Delete
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
