import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './css/PostList.css';

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Fetched posts:', data);
      setPosts(data);
    } catch (error) {
      console.error('Error fetching posts:', error);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleReadMoreClick = (postId, postTitle) => {
    console.log(`Clicked "Read More" for post ID: ${postId}, Title: ${postTitle}`);
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="post-list">
      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} className="post-item">
            {console.log('Rendering post:', post.id, post.title)}
            <div 
              className="post-thumbnail" 
              style={{ backgroundImage: `url(${post.imageUrl || '/default-thumbnail.png'})` }}
            ></div>
            <div className="post-content">
              <h3>{post.title}</h3>
              <p>{new Date(post.createdAt).toLocaleString()}</p>
              <Link to={`/post/${post.id}`}>
                <button onClick={() => handleReadMoreClick(post.id, post.title)}>
                  Read More
                </button>
              </Link>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
