import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown } from '@fortawesome/free-solid-svg-icons';
import './css/CommentItem.css';

const CommentItem = ({ comment, onReply, onLike, onDislike, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);

  useEffect(() => {
    // Check if the user has liked or disliked this comment
    if (user) {
      if (comment.likedUsers && comment.likedUsers.includes(user.userId)) {
        setIsLiked(true);
      }
      if (comment.dislikedUsers && comment.dislikedUsers.includes(user.userId)) {
        setIsDisliked(true);
      }
    }
  }, [user, comment.likedUsers, comment.dislikedUsers]);

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (user) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  const handleLikeClick = () => {
    if (user) {
      if (isDisliked) {
        setIsDisliked(false);
      }
      onLike(comment.id, isLiked);
      setIsLiked(!isLiked);
    } else {
      alert('Please log in to like this comment.');
    }
  };

  const handleDislikeClick = () => {
    if (user) {
      if (isLiked) {
        setIsLiked(false);
      }
      onDislike(comment.id, isDisliked);
      setIsDisliked(!isDisliked);
    } else {
      alert('Please log in to dislike this comment.');
    }
  };

  return (
    <div className="comment">
      <div className="comment-avatar" style={{ backgroundImage: `url(${comment.avatar})` }}></div>
      <div className="comment-content">
        <strong>{comment.author}</strong> <span>{comment.time}</span>
        <p>{comment.content}</p>
        <div className="comment-actions">
          <button
            className={`like-button ${isLiked ? 'liked' : ''}`}
            onClick={handleLikeClick}
            disabled={!user}
          >
            <FontAwesomeIcon icon={faThumbsUp} /> {comment.likes}
          </button>
          <button
            className={`dislike-button ${isDisliked ? 'disliked' : ''}`}
            onClick={handleDislikeClick}
            disabled={!user}
          >
            <FontAwesomeIcon icon={faThumbsDown} /> {comment.dislikes}
          </button>
          <button onClick={() => setShowReplyForm(!showReplyForm)} disabled={!user}>
            <FontAwesomeIcon icon={faReply} /> Reply
          </button>
        </div>
        {showReplyForm && (
          <form onSubmit={handleReplySubmit}>
            <input
              type="text"
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              placeholder="Write a reply..."
            />
            <button type="submit">Post</button>
          </form>
        )}
        {comment.replies && comment.replies.length > 0 && (
          <div className="replies">
            {comment.replies.map((reply) => (
              <CommentItem
                key={reply.id}
                comment={reply}
                onReply={onReply}
                onLike={onLike}
                onDislike={onDislike}
                user={user}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
