import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/PostForm.css'; // Make sure to import the CSS file

const NewPostPage = () => {
  const [post, setPost] = useState({ title: '', content: '', status: 'Draft' });
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPost({ ...post, [name]: value });
  };

  const handleSubmit = async (e) => {
      e.preventDefault();
      setIsLoading(true);
      setError('');

      try {
        const response = await fetch('http://localhost:5000/api/posts', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}` // Assuming you store the token in localStorage
          },
          body: JSON.stringify(post)
        });

        if (!response.ok) {
          throw new Error('Failed to create post');
        }

        const data = await response.json();
        console.log('Post created:', data);
        navigate('/admin'); // Redirect to admin page or wherever you want after successful creation
      } catch (error) {
        console.error('Error creating post:', error);
        setError('Failed to create post. Please try again.');
      } finally {
        setIsLoading(false);
      }
  };

  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="post-form-page">
      <h1>Create New Post</h1>
      <form className="post-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={post.title}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="content">Content:</label>
          <textarea
            id="content"
            name="content"
            value={post.content}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="status">Status:</label>
          <select
            id="status"
            name="status"
            value={post.status}
            onChange={handleInputChange}
            required
          >
            <option value="Draft">Draft</option>
            <option value="Published">Published</option>
            <option value="Scheduled">Scheduled</option>
          </select>
        </div>
        {post.status === 'Scheduled' && (
          <div className="form-group">
            <label htmlFor="scheduledDate">Scheduled Publish Date (Your Local Time):</label>
          <DatePicker
            selected={scheduledDate}
            onChange={handleDateChange}
            showTimeSelect
            timeFormat="HH:mm"
            timeIntervals={15}
            timeCaption="Time"
            dateFormat="MMMM d, yyyy HH:mm"
            className="form-control"
            minDate={new Date()}
            aria-label="Select date and time for scheduled publishing"
          />
          <div></div>
          <small className="form-text text-muted">
            Select the future date and time when you want this post to be automatically published.
          </small>
          {error && <div className="error-message">{error}</div>}
          </div>
        )}
        <button type="submit" className="submit-button">Create Post</button>
      </form>
    </div>
  );
};

export default NewPostPage;
