import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import CommentItem from './CommentItem';
import './css/CommentSection.css';

const socket = io('http://localhost:5000'); // Update with your backend URL

const CommentSection = ({ postId, user }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  const userId = user?.user?.userId || null;

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/comments/${postId}`);
        const data = await response.json();

        const processedComments = data.map(comment => ({
          ...comment,
          likedUsers: Array.isArray(comment.likedUsers) ? comment.likedUsers : [],
          replies: comment.replies || []  // Ensure each comment has a replies array
        }));

        setComments(processedComments);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchComments();

    socket.emit('joinPost', postId);

    socket.on('newComment', (comment) => {
      comment.likedUsers = Array.isArray(comment.likedUsers) ? comment.likedUsers : [];

      setComments((prevComments) => {
        if (comment.parentId) {
          // If it's a reply, find the parent comment and add the reply
          return prevComments.map(parentComment => 
            parentComment.id === comment.parentId 
              ? { ...parentComment, replies: [...parentComment.replies, comment] } 
              : parentComment
          );
        }
        return [...prevComments, comment];
      });
    });

    return () => {
      socket.emit('leavePost', postId);
      socket.off('newComment');
    };
  }, [postId]);

  const handleCommentSubmit = (e, parentId = null) => {
    e.preventDefault();

    if (newComment.trim()) {
      socket.emit('createComment', {
        postId,
        content: newComment,
        parentId: parentId || null,
        userId
      });

      setNewComment('');
    }
  };

  const handleReply = (parentId, replyText) => {
    if (replyText.trim()) {
      socket.emit('createComment', { 
        postId, 
        content: replyText, 
        parentId,
        userId
      });
    }
  };

  const handleLike = (commentId, isLiked) => {
    if (!userId) {
      alert('Please log in to like a comment.');
      return;
    }

    socket.emit('likeComment', { commentId, isLiked });

    setComments((prevComments) =>
      prevComments.map((comment) => {
        // Check for comment and its replies
        if (comment.id === commentId) {
          return {
            ...comment,
            likes: isLiked ? comment.likes - 1 : comment.likes + 1,
            likedUsers: isLiked
              ? comment.likedUsers.filter((id) => id !== userId)
              : [...comment.likedUsers, userId],
          };
        }

        // Update likes for replies
        const updatedReplies = comment.replies.map((reply) =>
          reply.id === commentId
            ? {
                ...reply,
                likes: isLiked ? reply.likes - 1 : reply.likes + 1,
                likedUsers: isLiked
                  ? reply.likedUsers.filter((id) => id !== userId)
                  : [...reply.likedUsers, userId],
              }
            : reply
        );
        return { ...comment, replies: updatedReplies };
      })
    );
  };

  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <ul>
        {comments.length > 0 ? (
          comments.map((comment, index) => (
            <CommentItem
              key={index}
              comment={comment}
              onReply={handleReply}
              onLike={handleLike}
              user={user}
            />
          ))
        ) : (
          <p>No comments available.</p>
        )}
      </ul>
      <form onSubmit={handleCommentSubmit}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
          disabled={!userId}
        />
        <button type="submit" disabled={!userId}>Post</button>
      </form>
    </div>
  );
};

export default CommentSection;
