import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import CommentSection from './CommentSection';
import DOMPurify from 'dompurify';
import './css/PostPage.css';

const PostPage = ({ user }) => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [surroundingPosts, setSurroundingPosts] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the current post
    fetch(`http://localhost:5000/api/posts/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => setPost(data))
      .catch((error) => setError(error.message));

    // Fetch surrounding posts
    fetchSurroundingPosts(id);
  }, [id]);

  // Function to fetch surrounding posts based on the current post ID
  const fetchSurroundingPosts = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/surrounding/${postId}`);
      const data = await response.json();
      setSurroundingPosts(data);
    } catch (error) {
      console.error('Error fetching surrounding posts:', error);
    }
  };

  // Handle click on a surrounding post and update the list based on the clicked post
  const handleSurroundingPostClick = (postId) => {
    fetchSurroundingPosts(postId);
    navigate(`/post/${postId}`);
  };

  if (error) return <div className="error-message">{error}</div>;
  if (!post) return <div className="loading-message">Loading...</div>;

  return (
    <div className="post-page-container">
      <div className="back-arrow" onClick={() => navigate('/')}>
        ← Back
      </div>

      <div className="post-page">
        <aside className="sidebar">
          <h2 className="sidebar-title">Recent Posts</h2>
          <ul className="recent-posts-list">
            {surroundingPosts.map((surroundingPost) => (
              <li
                key={surroundingPost.id}
                className={
                  surroundingPost.id === post.id ? 'active recent-post-item' : 'recent-post-item'
                }
              >
                {surroundingPost.id !== post.id ? (
                  <Link
                    to={`/post/${surroundingPost.id}`}
                    className="recent-post-link"
                    onClick={() => handleSurroundingPostClick(surroundingPost.id)}
                  >
                    {surroundingPost.title}
                  </Link>
                ) : (
                  <span className="current-post-link">{surroundingPost.title}</span>
                )}
              </li>
            ))}
          </ul>
        </aside>

        <div className="post-content">
          <div className="post-category">
            {post.category && <p>{post.category}</p>}
          </div>
          <h1 className="post-title">{post.title}</h1>
          <p className="post-date">
            {new Date(post.createdAt).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric',
              hour: '2-digit',
              minute: '2-digit',
            })}
          </p>
          <p className="view-count">👁 {post.readCount || 0} views</p>
          {post.imageUrl && (
            <div className="post-image-container">
              <img src={post.imageUrl} alt={post.title} className="post-image" />
            </div>
          )}
          <div
            dangerouslySetInnerHTML={{
              __html: DOMPurify.sanitize(post.content),
            }}
            className="post-text-content"
          />
          <CommentSection postId={id} user={user} />
        </div>
      </div>
    </div>
  );
};

export default PostPage;
