import React, { useState, useEffect } from 'react';
import './css/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [error, setError] = useState(''); // Error message state

  // Fetch suggestions when the user types
  const fetchSuggestions = async (query) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/search?searchTerm=${encodeURIComponent(query)}`);
      const data = await response.json();

      if (Array.isArray(data)) {
        setSuggestions(data.map(post => post.title)); // Only show titles as suggestions
      } else {
        setSuggestions([]); // Clear suggestions if the data format is invalid
      }
    } catch (error) {
      console.error('Error fetching search suggestions:', error);
      setSuggestions([]);  // Clear suggestions in case of an error
    }
  };

  const handleInputChange = (e) => {
    const query = e.target.value;
    setSearchTerm(query);

    // Fetch suggestions based on input or fetch all posts if input is empty
    if (query.length === 0) {
      setError('');
      fetchSuggestions(''); // Fetch all posts if input is empty
    } else if (query.length > 2) {
      setError('');
      fetchSuggestions(query); // Fetch filtered suggestions
    } else {
      setSuggestions([]);
      if (query.length > 0) {
        setError('Enter at least 2 characters to see suggestions');
      } else {
        setError('');
      }
    }
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.length > 2) {
      onSearch(searchTerm); // Trigger the search function
      setSuggestions([]);   // Clear suggestions after search
    } else {
      setError('Enter at least 2 characters to search'); // Set error if search is attempted with less than 2 characters
    }
  };

  // Handle clicking a suggestion
  const handleSuggestionClick = (suggestion) => {
    setSearchTerm(suggestion); // Set search term to the clicked suggestion
    onSearch(suggestion);      // Trigger search with the suggestion
    setSuggestions([]);        // Clear suggestions after selecting
  };

  return (
    <div className="search-container">
      <h2 className="search-title">FIND</h2>
      <form onSubmit={handleSubmit} className="search-bar">
        <input
          type="text"
          placeholder="Search posts..."
          value={searchTerm}
          onChange={handleInputChange}
          className="search-input"
        />
        <button type="submit" className="search-button">
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <circle cx="11" cy="11" r="8"></circle>
            <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
          </svg>
        </button>
      </form>

      {/* Display suggestions or error message in the same list */}
      <ul className="suggestions-list">
        {error && <li className="error-message">{error}</li>}
        {suggestions.length > 0 && suggestions.map((suggestion, index) => (
          <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
            {suggestion}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchBar;
