import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import CommentSection from './CommentSection';
import DOMPurify from 'dompurify';
import './css/PostPage.css';

const PostPage = ({ user }) => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [recentPosts, setRecentPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchPost(id);
    fetchRecentPosts(1);
  }, [id]);

  const fetchPost = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setPost(data);
    } catch (error) {
      setError(error.message);
    }
  };

  const fetchRecentPosts = async (page) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/recent?page=${page}&limit=5`);
      const data = await response.json();
      setRecentPosts(data.posts);
      setCurrentPage(data.currentPage);
      setTotalPages(data.totalPages);
    } catch (error) {
      console.error('Error fetching recent posts:', error);
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
      fetchRecentPosts(newPage);
    }
  };

  if (error) return <div className="error-message">{error}</div>;
  if (!post) return <div className="loading-message">Loading...</div>;

  return (
    <div className="post-page-container">
      <div className="back-arrow" onClick={() => navigate('/')}>
        ‚Üê Back
      </div>

      <div className="post-page">
        <aside className="sidebar">
          <h2 className="sidebar-title">Recent Posts</h2>
          <ul className="recent-posts-list">
            {recentPosts.map((recentPost) => (
              <li
                key={recentPost.id}
                className={recentPost.id === post.id ? 'active recent-post-item' : 'recent-post-item'}
              >
                <Link to={`/post/${recentPost.id}`} className="recent-post-link">
                  {recentPost.title}
                </Link>
              </li>
            ))}
          </ul>
          <div className="pagination">
            <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
              Previous
            </button>
            <span>{currentPage} / {totalPages}</span>
            <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
              Next
            </button>
          </div>
        </aside>

        <div className="post-content">
          {/* Post content remains the same */}
        </div>
      </div>
    </div>
  );
};

export default PostPage;
