import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp as solidThumbsUp, faTrash, faEllipsisV } from '@fortawesome/free-solid-svg-icons'; // Import the ellipsis (three dots) icon
import './css/Comment.css';

const CommentItem = ({ comment, onReply, onLike, onDelete, user }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyText, setReplyText] = useState('');
  const [showMenu, setShowMenu] = useState(false); // State for showing/hiding the dropdown menu
  const userId = user?.userId || null;

  const isLikedByCurrentUser = comment.isLikedByCurrentUser;

  const handleLikeClick = () => {
    if (userId) {
      onLike(comment.id, isLikedByCurrentUser);
    } else {
      alert('Please log in to like this comment.');
    }
  };

  const handleReplySubmit = (e) => {
    e.preventDefault();
    if (userId) {
      onReply(comment.id, replyText);
      setReplyText('');
      setShowReplyForm(false);
    } else {
      alert('Please log in to reply.');
    }
  };

  const handleDeleteClick = () => {
    if (window.confirm('Are you sure you want to delete this comment?')) {
      onDelete(comment.id); // Call the onDelete function passed from the parent
    }
  };

  const handleMenuToggle = () => {
    setShowMenu((prevState) => !prevState); // Toggle the dropdown menu on click
  };

  const avatarInitial = comment.user?.username ? comment.user.username.charAt(0).toUpperCase() : '?';

  const generateColor = (input) => {
    const colors = ['#D8E0BB', '#B6CEC7', '#86A3C3', '#7268A6', '#6B3074'];
    const index = input.charCodeAt(0) % colors.length;
    return colors[index];
  };

  const formatCommentTime = (createdAt) => {
    const now = new Date();
    const commentTime = new Date(createdAt);
    const diffInSeconds = Math.floor((now - commentTime) / 1000);

    if (diffInSeconds < 60) {
      return 'just now';
    } else if (diffInSeconds < 3600) {
      const minutes = Math.floor(diffInSeconds / 60);
      return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;
    } else if (diffInSeconds < 86400) {
      const hours = Math.floor(diffInSeconds / 3600);
      return `${hours} hour${hours > 1 ? 's' : ''} ago`;
    } else {
      return commentTime.toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' });
    }
  };

  const formattedTime = formatCommentTime(comment.createdAt);

  return (
    <div className="comment-container">
      <div className="comment">
        <div
          className="comment-avatar"
          style={{ backgroundColor: generateColor(comment.user?.userId || 'default') }}
        >
          <span className="avatar-initial">{avatarInitial}</span>
        </div>
        <div className="comment-content">
          <strong>{comment.user?.username || 'Anonymous'}</strong>
          <span className="comment-time">{formattedTime}</span>
          <p>{comment.content}</p>
          <div className="comment-actions">
            <span
              className={`like-button ${isLikedByCurrentUser ? 'liked' : ''}`}
              onClick={handleLikeClick}
            >
              <FontAwesomeIcon icon={solidThumbsUp} /> {comment.likeCount || 0}
            </span>
            {comment.parentId === null && (
              <span className="reply-text" onClick={() => setShowReplyForm(!showReplyForm)} disabled={!userId}>
                Reply
              </span>
            )}
          </div>

          {showReplyForm && (
            <form onSubmit={handleReplySubmit} className="reply-form">
              <input
                type="text"
                value={replyText}
                onChange={(e) => setReplyText(e.target.value)}
                placeholder="Write a reply..."
              />
              <button type="submit">Post</button>
            </form>
          )}

          {comment.replies && comment.replies.length > 0 && (
            <div className="replies">
              {comment.replies.map((reply) => (
                <div key={reply.id} className="reply-item">
                  <div className="reply-line"></div>
                  <CommentItem
                    comment={reply}
                    onLike={onLike}
                    onReply={onReply}
                    onDelete={onDelete}
                    user={user}
                  />
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Conditionally render the three-dot menu if the user is the comment author */}
        {comment.userId === userId && (
          <div className="menu-container">
            <span className="menu-icon" onClick={handleMenuToggle}>
              <FontAwesomeIcon icon={faEllipsisV} />
            </span>
            {showMenu && (
              <div className="dropdown-menu">
                <div className="menu-item" onClick={() => alert('Edit clicked')}>
                  Edit
                </div>
                <div className="menu-item delete" onClick={handleDeleteClick}>
                  Delete
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentItem;
