import React, { useState, useEffect } from 'react';

const AdminPage = () => {
  const [posts, setPosts] = useState([]);
  const [analytics, setAnalytics] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '', author: '' });
  const [editingPost, setEditingPost] = useState(null);

  useEffect(() => {
    // Fetch posts and analytics data
    fetchPosts();
    fetchAnalytics();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('/api/admin/posts'); // Ensure this route exists in backend
      const data = await response.json();
      setPosts(data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  const fetchAnalytics = async () => {
    try {
      const response = await fetch('/api/admin/analytics'); // Analytics data from backend
      const data = await response.json();
      setAnalytics(data);
    } catch (error) {
      console.error('Error fetching analytics:', error);
    }
  };

  const handleNewPostChange = (e) => {
    setNewPost({ ...newPost, [e.target.name]: e.target.value });
  };

  const createPost = async (e) => {
    e.preventDefault();
    try {
      await fetch('/api/admin/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newPost),
      });
      fetchPosts(); // Refresh the post list after creation
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  const updatePost = async (postId) => {
    try {
      await fetch(`/api/admin/posts/${postId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingPost),
      });
      fetchPosts(); // Refresh the post list after update
    } catch (error) {
      console.error('Error updating post:', error);
    }
  };

  const startEditPost = (post) => {
    setEditingPost(post);
  };

  return (
    <div className="admin-page">
      <h1>Admin Page</h1>

      {/* Post Management Section */}
      <section>
        <h2>Create New Post</h2>
        <form onSubmit={createPost}>
          <input
            type="text"
            name="title"
            value={newPost.title}
            onChange={handleNewPostChange}
            placeholder="Post Title"
          />
          <textarea
            name="content"
            value={newPost.content}
            onChange={handleNewPostChange}
            placeholder="Post Content"
          ></textarea>
          <input
            type="text"
            name="author"
            value={newPost.author}
            onChange={handleNewPostChange}
            placeholder="Author"
          />
          <button type="submit">Create Post</button>
        </form>
      </section>

      {/* Post Editing Section */}
      <section>
        <h2>Edit Existing Posts</h2>
        <ul>
          {posts.map((post) => (
            <li key={post.id}>
              <strong>{post.title}</strong> - {post.author}
              <button onClick={() => startEditPost(post)}>Edit</button>
            </li>
          ))}
        </ul>
        {editingPost && (
          <div>
            <h3>Edit Post: {editingPost.title}</h3>
            <input
              type="text"
              value={editingPost.title}
              onChange={(e) => setEditingPost({ ...editingPost, title: e.target.value })}
            />
            <textarea
              value={editingPost.content}
              onChange={(e) => setEditingPost({ ...editingPost, content: e.target.value })}
            ></textarea>
            <button onClick={() => updatePost(editingPost.id)}>Update Post</button>
          </div>
        )}
      </section>

      {/* Analytics Section */}
      <section>
        <h2>Analytics</h2>
        <ul>
          {analytics.map((data) => (
            <li key={data.id}>
              <strong>{data.metric}</strong>: {data.value}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default AdminPage;
