import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/LoginPage.css';
import jwt_decode from 'jwt-decode';

const LoginPage = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null); 
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token); // Store the token in localStorage

        // Decode the token to get user info
        const decoded = jwt_decode(data.token);
        localStorage.setItem('user', JSON.stringify(decoded)); // Store user data in localStorage

        onLogin(decoded); // Pass the decoded user data to the parent component

        navigate('/'); // Use navigate instead of history.push
      } else {
        const errorData = await response.json();
        setError(errorData.error); 
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('An unexpected error occurred during login.');
    }
  };

  const navigateToRegister = () => {
    navigate('/register'); // Use navigate instead of history.push
  };

  return (
    <div className="container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div className="input-group">
          <label>Username</label>
          <input 
            type="text" 
            value={username} 
            onChange={(e) => setUsername(e.target.value)} 
          />
        </div>
        <div className="input-group">
          <label>Password</label>
          <input 
            type="password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
          />
        </div>
        <div className="button-group">
          <button type="submit">Log In</button>
        </div>
      </form>
      {error && <div className="error-message">{error}</div>} 
      <div className="text-center">
        <a href="#" onClick={navigateToRegister}>Don't have an account? Register</a>
      </div>
    </div>
  );
};

export default LoginPage;
